import React, { useState } from 'react';
import Cards from '../components/Cards';
import { motion } from 'framer-motion';

const cards = [
  <Cards key="1" />,
  <Cards key="2" />,
  <Cards key="3" />,
  <Cards key="4" />,
  <Cards key="5" />,
  <Cards key="6" />,
];

function SectionTwo() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? cards.length - 1 : prevIndex - 1));
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex === cards.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <section className="h-screen flex justify-center items-center snap-center scroll-smooth relative">
      <motion.div
        className="flex justify-center items-center"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        {cards[currentIndex]}
      </motion.div>
      <button
        onClick={handlePrev}
        className="absolute left-0 p-2 bg-blue-600 hover:bg-blue-700 text-white rounded-full"
      >
        Prev
      </button>
      <button
        onClick={handleNext}
        className="absolute right-0 p-2 bg-blue-600 hover:bg-blue-700 text-white rounded-full"
      >
        Next
      </button>
    </section>
  );
}

export default SectionTwo;
