import React, { useState, useEffect } from 'react';

function Header() {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      setIsScrolled(scrollTop > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className={`flex justify-center items-center ${isScrolled ? 'fixed top-0 w-full h-16 bg-white shadow-md' : 'h-screen'} transition-all duration-500`}>
      <h1 className={`font-bold underline font-playwrite ${isScrolled ? 'text-2xl' : 'text-6xl'} text-blue-900 hover:text-green-500 transition-all duration-500`}>
        Shreyash Shubh
      </h1>
    </div>
  );
}

export default Header;
